def FindLargestProduct(lst,n,k,l):

	length=0
	mult=1
	if k+l>len(n):
		lst.sort()
		lst.reverse()
		print(lst[0])
	else:
		for i in xrange(k,k+l):
			mult=mult*int(n[i])
			length+=1
			if k>len(n):
				break
			if length==l:
				lst.append(mult)
				k+=1
				FindLargestProduct(lst,n,k,l)

if __name__=='__main__':
	lst=[]
	n=''
	k=0
	l=13
	FindLargestProduct(lst,n,k,l)